ui-tree.script:
context.variables.set('textVariable', new ValidatedVariable('', null, value => {
    context.ids.get('label').text = `Text input: ${value}`;
}, false));
context.variables.set('button1Callback', () => {
    context.ids.get('label').text = 'Button 1 clicked!';
});
context.variables.set('button2Callback', () => {
    context.ids.get('label').text = 'Button 2 clicked!';
});
